
public class SCops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n, d, q;
		
		n = 20;
		d = 2;
		
		if (d != 0 && (n % d) == 0) {
			System.out.println(d + " is a factor of " + n);
		}
		
		d = 0;
		
		if (d != 0 && (n % d) == 0) {
			System.out.println(d + " is a factor of " + n);
		}
		
		if (d != 0 & (n % d) == 0) {
			System.out.println(d + " is a factor of " + n);
		}
	}

}
